export default function TextForm(props) {

    const handleUpClick = ()=> {
        console.log("Uppercase was clicked");
        setText("You have clicked on handleUpClick")
    }

    const handleOnChange = ()=> {
        console.log("On Change");
    }




    const [text, setText] = useState('Enter text here');
    // text = "Change the text" // This is the wrong way to update the text in ReactJs 
    // setText("Change the text here") // This is the correct way to update the text in ReactJs 

    return (
        <div>
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" id="myBox" rows="6" value={text} onChange = {handleOnChange} id="myBox" ></textarea>
            </div>
            <button className="btm btn-primary">Convert To Upercase</button>

        </div>
    )
}
